mapper.select:
SELECT t.person_id as personId, t.order_id as orderId, t.beneficial_rate as beneficialRate, t.beneficial_name as beneficialName, t.sex, t.birth_date as birthDate, t.relationship, t.cert_type as certType, t.cert_no as certNo, t.validity_date as validityDate, t.residential_address as residentialAddress, t.beneficiary_order as beneficiaryOrder, t.create_time as createTime, t.description, t.remark1, t.remark2, t.remark3 FROM beneficial t
and t.person_id = #{personId}
and t.beneficial_name = #{userName}
and t.cert_type = #{certType}
and t.cert_no = #{certNo}

mapper.update:
 update beneficial set 
 	 person_id=#{personId}
	, order_id=#{orderId}
	, beneficial_rate=#{beneficialRate}
	, beneficial_name=#{beneficialName}
	 ,sex=#{sex}
	, birth_date=#{birthDate}
	, relationship=#{relationship}
	 ,cert_type=#{certType}
	, cert_no=#{certNo}
	, validity_date=#{validityDate}
	, residential_address=#{residentialAddress}
	, beneficiary_order=#{beneficiaryOrder}
	 ,create_time=#{createTime}
	 ,description=#{description}
	 ,remark1=#{remark1}
	, remark2=#{remark2}
	, remark3=#{remark3}
	where person_id=#{personId}
