mapper.select:
SELECT m.USER_ID as userId,m.USER_NAME as userName,m.TELEPHONE as telephone, m.WECHAT_NUM as wechatNum,m.PROVINCE as province,m.USER_ADDR as userAddr, mg.MERCHANT_NAME as merchantName, m1.USER_NAME AS recommender,m2.USER_NAME AS dirRecommender, m.CREATE_TIME as createTime,m.UPDATE_TIME as updateTime,m.STATUS as status FROM t_member_basicinfo m LEFT JOIN t_merchant_manage mg ON mg.MERCHANT_ID = m.MERCHANT_ID LEFT JOIN t_member_basicinfo m1 ON m.RECOMMENDER = m1.USER_ID LEFT JOIN t_member_basicinfo m2 ON m.DIR_RECOMMENDER = m2.USER_ID
and m.USER_NAME like CONCAT('%',#{USER_NAME},'%')
and m.TELEPHONE = #{TELEPHONE}
and m.WECHAT_NAME like CONCAT('%',#{WECHAT_NAME},'%')
and m.WECHAT_NUM = #{WECHAT_NUM}

SELECT m.USER_ID as userId,m.USER_NAME as userName,m.CAR_NUM as carNum,m.CAR_FRAME_NUM as carFrameNum, m.CHANG_TYPE as changType,m.REGISTER_TIME as registerTime,m.STATUS as status,m.TELEPHONE as telephone FROM t_customer_basicinfo m
and m.USER_NAME like CONCAT('%',#{USER_NAME},'%')
and m.TELEPHONE = #{TELEPHONE}
