mapper.select:
SELECT t.ORDER_ID AS orderId, t.CREATE_TIME AS createTime, t.ORDER_TYPE AS orderType, m.category_name as orderTypeName, p.USER_NAME as customerName, s.user_name as salesMan, f.MERCHANT_NAME as merchantName, p.PROVINCE as province, p.CITY as city, p.COUNTRY as country, t.ORDER_FLAG AS orderFlag FROM order_info t LEFT JOIN t_customer_basicinfo p ON t.USER_ID = p.USER_ID LEFT JOIN t_merchant_manage f ON t.MERCHANT_ID = f.MERCHANT_ID LEFT JOIN t_member_basicinfo s ON t.sales_man = s.USER_ID LEFT JOIN t_merchant_product_category m ON t.order_type = m.category_id
t.ORDER_ID is not null
and t.CREATE_TIME >= #{createStartTime}
and t.CREATE_TIME <= #{createEndTime}
and p.PROVINCE = #{province}
and p.CITY = #{city}
and p.COUNTRY = #{country}
and t.ORDER_TYPE = #{orderTypeQuery}
and ${keywordType} like CONCAT('%',#{keywordInfo},'%')
limit #{start},#{rows}

		SELECT
			count(*)
		FROM
			order_info t
		LEFT JOIN t_customer_basicinfo p ON t.USER_ID = p.USER_ID
		LEFT JOIN t_merchant_manage f ON t.MERCHANT_ID = f.MERCHANT_ID
		LEFT JOIN t_member_basicinfo s ON t.sales_man = s.USER_ID
		LEFT JOIN t_merchant_product_category m ON t.order_type = m.category_id

			t.ORDER_ID is not null

                and t.CREATE_TIME >= #{createStartTime}  


                 and t.CREATE_TIME <= #{createEndTime} 


                  and p.PROVINCE = #{province}  


                 and p.CITY = #{city}  


                  and p.COUNTRY = #{country}  


                  and t.ORDER_TYPE = #{orderTypeQuery}  


                  and ${keywordType} like CONCAT('%',#{keywordInfo},'%')
