mapper.select:
SELECT t.ORDER_ID AS orderId, t.CREATE_TIME AS createTime, m.category_name as orderTypeName, t.POLICYHOLDER_ID as POLICYHOLDER_ID, p.USER_NAME as customerName, t.RECORDER AS operatorName, s.user_name as salesMan, f.MERCHANT_NAME as merchantName, TT.NAME as province, t.ORDER_FLAG AS orderFlag FROM order_sx t LEFT JOIN user o ON t.recorderid = o.id LEFT JOIN policyholder p ON t.POLICYHOLDER_ID = p.PID LEFT JOIN t_merchant_manage f ON t.MERCHANT_ID = f.MERCHANT_ID LEFT JOIN t_member_basicinfo s ON t.sales_man = s.USER_ID LEFT JOIN user b ON t.OPERATOR = b.id LEFT JOIN t_merchant_product_category m ON t.order_type = m.category_id LEFT JOIN t_china_area TT ON p.PPROVINCE = TT.ID
t.ORDER_ID is not null
and t.CREATE_TIME >= #{createStartTime}
and t.CREATE_TIME <= #{createEndTime}
and p.PPROVINCE = #{province}
and p.PCITY = #{city}
and p.PDISTRICT = #{country}
and ${keywordType} like CONCAT('%',#{keywordInfo},'%')
limit #{start},#{rows}

SELECT count(*) FROM order_sx t LEFT JOIN user o ON t.recorderid = o.id LEFT JOIN policyholder p ON t.POLICYHOLDER_ID = p.PID LEFT JOIN t_merchant_manage f ON t.MERCHANT_ID = f.MERCHANT_ID LEFT JOIN t_member_basicinfo s ON t.sales_man = s.USER_ID LEFT JOIN user b ON t.OPERATOR = b.id LEFT JOIN t_merchant_product_category m ON t.order_type = m.category_id
t.ORDER_ID is not null
and t.CREATE_TIME >= #{createStartTime}
and t.CREATE_TIME <= #{createEndTime}
and p.PPROVINCE = #{province}
and p.PCITY = #{city}
and p.PDISTRICT = #{country}
and ${keywordType} like CONCAT('%',#{keywordInfo},'%')
